echo "Generating function prototypes..."
avr-g++ -c -g -Os -w -std=gnu++11 -fpermissive -fno-exceptions -ffunction-sections -fdata-sections -fno-threadsafe-statics  -flto -w -x c++ -E -CC -mmcu=atmega328p -DF_CPU=8000000L -DARDUINO=10611 -DARDUINO_AVR_SMART_FARM -DARDUINO_ARCH_AVR   "-I$SMARTFARM/include/cores/smartfarm" "-I$SMARTFARM/include/variants/smartfarm" "$1" -o "$SMARTFARM/tmp/preproc/ctags_target_for_gcc_minus_e.cpp"
