avr-g++ -c -g -Os -w -std=gnu++11 -fpermissive -fno-exceptions -ffunction-sections -fdata-sections -fno-threadsafe-statics -MMD -flto -mmcu=atmega328p -DF_CPU=8000000L -DARDUINO=10611 -DARDUINO_AVR_SMART_FARM -DARDUINO_ARCH_AVR   "-I$SMARTFARM/include/cores/smartfarm" "-I$SMARTFARM/include/variants/smartfarm" "$1" -o "$1.o"
echo "Compiling libraries..."
echo "Compiling core..."
avr-gcc -c -g -x assembler-with-cpp -flto -mmcu=atmega328p -DF_CPU=8000000L -DARDUINO=10611 -DARDUINO_AVR_SMART_FARM -DARDUINO_ARCH_AVR   "-I$SMARTFARM/include/cores/smartfarm" "-I$SMARTFARM/include/variants/smartfarm" "$SMARTFARM/include/cores/smartfarm/wiring_pulse.S" -o "$SMARTFARM/tmp/core/wiring_pulse.S.o"
