avr-g++ -c -g -Os -w -std=gnu++11 -fpermissive -fno-exceptions -ffunction-sections -fdata-sections -fno-threadsafe-statics -MMD -flto -mmcu=atmega328p -DF_CPU=8000000L -DARDUINO=10611 -DARDUINO_AVR_SMART_FARM -DARDUINO_ARCH_AVR   "-I$SMARTFARM/include/cores/smartfarm" "-I$SMARTFARM/include/variants/smartfarm" "$SMARTFARM/include/cores/smartfarm/IPAddress.cpp" -o "$SMARTFARM/tmp/core/IPAddress.cpp.o"
