avr-gcc-ar rcs  "$SMARTFARM/tmp/core/core.a" "$SMARTFARM/tmp/core/wiring_pulse.S.o"
avr-gcc-ar rcs  "$SMARTFARM/tmp/core/core.a" "$SMARTFARM/tmp/core/WInterrupts.c.o"
avr-gcc-ar rcs  "$SMARTFARM/tmp/core/core.a" "$SMARTFARM/tmp/core/hooks.c.o"
avr-gcc-ar rcs  "$SMARTFARM/tmp/core/core.a" "$SMARTFARM/tmp/core/wiring.c.o"
avr-gcc-ar rcs  "$SMARTFARM/tmp/core/core.a" "$SMARTFARM/tmp/core/wiring_analog.c.o"
avr-gcc-ar rcs  "$SMARTFARM/tmp/core/core.a" "$SMARTFARM/tmp/core/wiring_digital.c.o"
avr-gcc-ar rcs  "$SMARTFARM/tmp/core/core.a" "$SMARTFARM/tmp/core/wiring_pulse.c.o"
avr-gcc-ar rcs  "$SMARTFARM/tmp/core/core.a" "$SMARTFARM/tmp/core/wiring_shift.c.o"
