avr-gcc -w -Os -flto -fuse-linker-plugin -Wl,--gc-sections -mmcu=atmega328p  -o "upload.ino.elf" "$1.o" "$SMARTFARM/tmp/core/core.a" "-L/smartfarm_sketch" -lm
avr-objcopy -O ihex -j .eeprom --set-section-flags=.eeprom=alloc,load --no-change-warnings --change-section-lma .eeprom=0  "upload.ino.elf" "upload.ino.eep"
avr-objcopy -O ihex -R .eeprom  "upload.ino.elf" "upload.ino.hex"

